@namespace
class SpriteKind:
    Football = SpriteKind.create()
    helmet = SpriteKind.create()

def on_on_overlap(sprite, otherSprite):
    otherSprite.destroy()
sprites.on_overlap(SpriteKind.helmet, SpriteKind.enemy, on_on_overlap)

def on_a_pressed():
    mySprite4.set_position(returner.x, returner.y)
    mySprite4.follow(mySprite2, 100)
controller.A.on_event(ControllerButtonEvent.PRESSED, on_a_pressed)

def on_on_overlap2(sprite, otherSprite):
    mySprite.destroy()
    info.start_countdown(15)
sprites.on_overlap(SpriteKind.player, SpriteKind.Football, on_on_overlap2)

def on_on_overlap3(sprite, otherSprite):
    game.over(False)
sprites.on_overlap(SpriteKind.player, SpriteKind.enemy, on_on_overlap3)

